(36, 1011, NULL, NULL, '<h2 style="text-align:center"><strong>TEMPAT PENDAFTARAN WILAYAH ADAT&nbsp;</strong></h2>\n\n<p style="text-align: center;"><strong><img alt="" border="0" hspace="0" src="data:image/jpeg;base64," style="border:0px solid black; height:407px; margin-bottom:0px; margin-left:0px; margin-right:0px; margin-top:0px; width:575px" vspace="0" /></strong></p>\n', NULL, '2015-01-22 10:19:39', '2015-04-21 09:19:30', 1, NULL, NULL, '', NULL, 0);
